import React, { useState, useRef, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar, Line } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);


// Add this new component after your existing imports
const ScrollIndicator = ({ showIndicator }) => {
  if (!showIndicator) return null;

  return (
    <div className="fixed right-12 top-1/2 -translate-y-1/2 flex flex-col items-center animate-fade-in">
      <div className="animate-bounce flex flex-col items-center">
        <svg 
          width="24" 
          height="24" 
          viewBox="0 0 24 24" 
          fill="none" 
          stroke="currentColor" 
          strokeWidth="2" 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          className="text-blue-500"
        >
          <line x1="12" y1="5" x2="12" y2="19"></line>
          <polyline points="19 12 12 19 5 12"></polyline>
        </svg>
        <p className="text-sm font-medium text-gray-500 mt-2">Scroll Down</p>
      </div>
    </div>
  );
};

// Helper function to generate random colors
const getRandomColor = () => {
  const r = Math.floor(Math.random() * 255);
  const g = Math.floor(Math.random() * 255);
  const b = Math.floor(Math.random() * 255);
  return `rgba(${r}, ${g}, ${b}, 0.6)`;
};

// Helper function to check if data is valid
const isValidArray = (arr) => Array.isArray(arr) && arr.length > 0;

// Helper function to safely access object properties
const safeGet = (obj, path, defaultValue = '') => {
  try {
    return path.split('.').reduce((acc, part) => acc?.[part], obj) ?? defaultValue;
  } catch {
    return defaultValue;
  }
};

// Helper function to sort bar data
const sortBarData = (labels, data) => {
  if (!isValidArray(labels) || !isValidArray(data) || labels.length !== data.length) {
    return { sortedLabels: labels || [], sortedData: data || [] };
  }
  const pairs = labels.map((label, i) => [label, data[i]]);
  pairs.sort((a, b) => b[1] - a[1]);
  return {
    sortedLabels: pairs.map(pair => pair[0]),
    sortedData: pairs.map(pair => pair[1])
  };
};

const generateDistinctColors = (count) => {
  const hueStep = 360 / count;
  return Array.from({ length: count }, (_, i) => {
    const hue = i * hueStep;
    return `hsla(${hue}, 70%, 60%, 0.6)`;
  });
};

// Group data by sender for multi-line charts
const groupBySender = (data, dateField='date', valueField='message_count') => {
  if (!isValidArray(data)) return { dates: [], datasets: [] };

  try {
    const senders = [...new Set(data.map(d => safeGet(d, 'sender')))].filter(Boolean);
    const dates = [...new Set(data.map(d => safeGet(d, dateField)))].filter(Boolean).sort();

    // Generate distinct colors based on number of senders
    const colors = generateDistinctColors(senders.length);

    const datasets = senders.map((sender, index) => {
      const senderData = data.filter(d => safeGet(d, 'sender') === sender);
      const values = dates.map(dt => {
        const entry = senderData.find(x => safeGet(x, dateField) === dt);
        return safeGet(entry, valueField, 0);
      });

      const color = colors[index];

      return {
        label: sender,
        data: values,
        borderColor: color,
        backgroundColor: color,
        fill: true
      };
    });

    return { dates, datasets };
  } catch (error) {
    console.error('Error in groupBySender:', error);
    return { dates: [], datasets: [] };
  }
};

// Build chart datasets from analysisData
const buildChartDataSets = (analysisData) => {
  if (!analysisData) return [];

  const chartDataSets = [];

  try {
    // Day of Week Chart
    if (isValidArray(analysisData.day_of_week_data)) {
      const labels = analysisData.day_of_week_data.map(d => safeGet(d, 'day_of_week'));
      const values = analysisData.day_of_week_data.map(d => safeGet(d, 'avg_messages', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Average Messages by Day of Week",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: 'Avg Messages by Day of Week',
            data: sortedData,
            backgroundColor: 'rgba(54, 162, 235, 0.6)',
          }]
        },
        commentary: "See which days of the week are most active for chatting!"
      });
    }

    // Monthly Messages Chart
    if (isValidArray(analysisData.monthly_messages_data)) {
      chartDataSets.push({
        title: "Monthly Message Count",
        type: "Line",
        data: {
          labels: analysisData.monthly_messages_data.map(d => safeGet(d, 'date')),
          datasets: [{
            label: 'Messages per Month',
            data: analysisData.monthly_messages_data.map(d => safeGet(d, 'message_count', 0)),
            borderColor: 'rgba(255, 99, 132, 1)',
            fill: false
          }]
        },
        commentary: "Track how chat activity changes month by month"
      });
    }

    // Average Messages Sent Chart
    if (isValidArray(analysisData.avg_messages_sent_data)) {
      chartDataSets.push({
        title: "Average Messages Sent per Month",
        type: "Line",
        data: {
          labels: analysisData.avg_messages_sent_data.map(d => safeGet(d, 'month')),
          datasets: [{
            label: 'Avg Messages per Month',
            data: analysisData.avg_messages_sent_data.map(d => safeGet(d, 'avg_messages_sent', 0)),
            borderColor: 'rgba(75, 192, 192, 1)',
            fill: false
          }]
        },
        commentary: "See the average number of messages sent each month"
      });
    }

    // Quarterly Contribution Chart
    if (isValidArray(analysisData.quarterly_contribution_data)) {
      const quarterData = groupBySender(analysisData.quarterly_contribution_data, 'date', 'message_count');
      if (quarterData.dates.length > 0 && quarterData.datasets.length > 0) {
        chartDataSets.push({
          title: "Quarterly Message Contribution by Sender",
          type: "Line",
          data: {
            labels: quarterData.dates,
            datasets: quarterData.datasets
          },
          commentary: "Compare how different people contribute to the conversation over quarters"
        });
      }
    }

    // Yearly Comparison Chart
    if (isValidArray(analysisData.yearly_comparison_data)) {
      const labels = analysisData.yearly_comparison_data.map(d => safeGet(d, 'sender'));
      const values = analysisData.yearly_comparison_data.map(d => safeGet(d, 'percent_change', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Year-Over-Year Message Change",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: 'YoY % Change',
            data: sortedData,
            backgroundColor: 'rgba(153, 102, 255, 0.6)'
          }]
        },
        commentary: "See how each person's message frequency changed compared to last year"
      });
    }

    // Top Ten Days Chart
    if (isValidArray(analysisData.top_ten_days_data)) {
      const labels = analysisData.top_ten_days_data.map(d => safeGet(d, 'day'));
      const values = analysisData.top_ten_days_data.map(d => safeGet(d, 'chats', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Top 10 Most Active Days",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: 'Number of Messages',
            data: sortedData,
            backgroundColor: 'rgba(255, 206, 86, 0.6)'
          }]
        },
        commentary: "The days with the most messages - what happened on these days?"
      });
    }

    // Manic Data Chart
    if (isValidArray(analysisData.manic_data)) {
      const labels = analysisData.manic_data.map(d => safeGet(d, 'sender'));
      const values = analysisData.manic_data.map(d => safeGet(d, 'percent_manic', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Late Night Message Percentage",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: '% Messages (10pm-4am)',
            data: sortedData,
            backgroundColor: 'rgba(54, 162, 235, 0.6)'
          }]
        },
        commentary: "Who's the night owl? See who messages most during late hours"
      });
    }

    // Swear Data Chart
    if (isValidArray(analysisData.swear_data)) {
      const labels = analysisData.swear_data.map(d => safeGet(d, 'sender'));
      const values = analysisData.swear_data.map(d => safeGet(d, 'swears_per_message', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Swear Word Frequency",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: 'Swears per Message',
            data: sortedData,
            backgroundColor: 'rgba(255, 99, 132, 0.6)'
          }]
        },
        commentary: "Check out who has the most colorful vocabulary!"
      });
    }

    // Hangout Data Chart
    if (isValidArray(analysisData.hangout_data)) {
      const labels = analysisData.hangout_data.map(d => safeGet(d, 'sender'));
      const values = analysisData.hangout_data.map(d => safeGet(d, 'hangouts_per_message', 0));
      const { sortedLabels, sortedData } = sortBarData(labels, values);

      chartDataSets.push({
        title: "Hangout Discussion Frequency",
        type: "Bar",
        data: {
          labels: sortedLabels,
          datasets: [{
            label: 'Hangout References per Message',
            data: sortedData,
            backgroundColor: 'rgba(75, 192, 192, 0.6)'
          }]
        },
        commentary: "Who's always trying to plan the next meetup?"
      });
    }

  } catch (error) {
    console.error('Error building chart datasets:', error);
  }

  return chartDataSets;
};

const AnimatedChart = ({ chart, isVisible, index }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    // Cleanup function to destroy chart instance
    return () => {
      const chartInstance = chartRef?.current?.chartInstance;
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, [chart]); // Add chart as a dependency so effect runs when chart changes

  if (!chart) return null;

  const isEven = index % 2 === 0;

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    scales: {
      x: {
        type: 'category',
        display: true,
      },
      y: {
        type: 'linear',
        display: true,
      }
    },
    plugins: {
      legend: {
        position: 'top',
        labels: { font: { family: 'Inter' } }
      }
    },
    animation: {
      duration: 1000,
      easing: 'easeInOutQuart'
    }
  };

  return (
    <div className={`transition-all duration-700 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-20'}`}>
      <div className="bg-white/50 backdrop-blur-sm shadow-xl w-full max-w-6xl mx-auto rounded-xl">
        <div className="p-6">
          <h2 className="text-2xl font-bold mb-6 text-center">{chart.title}</h2>
          <div className="flex flex-row items-center gap-8">
            {!isEven && (
              <div className="w-1/4 shrink-0">
                <p className="text-gray-600 text-lg leading-relaxed">{chart.commentary}</p>
              </div>
            )}
            <div className={`${isEven ? 'w-3/4' : 'w-3/4'} aspect-[16/9]`}>
              {chart.type === "Bar" ? (
                <Bar 
                  data={chart.data}
                  options={chartOptions}
                  ref={chartRef}
                />
              ) : (
                <Line 
                  data={chart.data}
                  options={chartOptions}
                  ref={chartRef}
                />
              )}
            </div>
            {isEven && (
              <div className="w-1/4 shrink-0">
                <p className="text-gray-600 text-lg leading-relaxed">{chart.commentary}</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};


const WhatsAppConnector = ({ onChatSelected }) => {
  const [sessionId, setSessionId] = useState(null);
  const [qrCode, setQrCode] = useState(null);
  const [status, setStatus] = useState('idle');
  const [error, setError] = useState(null);

  const initializeConnection = async () => {
    try {
      setStatus('connecting');
      const response = await fetch('http://localhost:3001/connect-whatsapp', {
        method: 'POST'
      });

      const data = await response.json();

      if (data.qrCode && data.sessionId) {
        setQrCode(data.qrCode);
        setSessionId(data.sessionId);
        setStatus('awaiting_scan');
      }
    } catch (error) {
      setError(error.message);
      setStatus('error');
    }
  };

  return (
    <div className="space-y-4">
      {error && (
        <div className="p-4 bg-red-50 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {qrCode ? (
        <div className="text-center">
          <img 
            src={`data:image/png;base64,${qrCode}`} 
            alt="WhatsApp QR Code"
            className="mx-auto mb-4"
          />
          <p className="text-sm text-gray-600">
            Scan this QR code with WhatsApp
          </p>
        </div>
      ) : (
        <button
          onClick={initializeConnection}
          disabled={status === 'connecting'}
          className="w-full px-4 py-3 bg-green-600 text-white rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-green-700 transition-colors"
        >
          {status === 'connecting' ? 'Connecting...' : 'Connect to WhatsApp'}
        </button>
      )}
    </div>
  );
};


const App = () => {
  const [phase, setPhase] = useState('upload');
  const [connectionMethod, setConnectionMethod] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [currentChartIndex, setCurrentChartIndex] = useState(0);
  const [isScrolling, setIsScrolling] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) return;

    try {
      setPhase('loading');
      const reader = new FileReader();

      reader.onload = async (ev) => {
        try {
          const response = await fetch('http://localhost:8000/analyze', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ transcript: ev.target.result })
          });

          if (!response.ok) throw new Error('Analysis failed');

          const json = await response.json();
          setAnalysisData(json);
          setPhase('visualize');
          setCurrentChartIndex(0);
        } catch (error) {
          console.error('Error processing file:', error);
          setError('Analysis failed');
          setPhase('upload');
        }
      };

      reader.readAsText(selectedFile);
    } catch (error) {
      console.error("Upload Error:", error);
      setError('File upload failed');
      setPhase('upload');
    }
  };

  const handleWhatsAppChat = async (sessionId, chatId) => {
    try {
      setPhase('loading');

      const response = await fetch('http://localhost:3001/analyze-whatsapp-chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ sessionId, chatId })
      });

      if (!response.ok) throw new Error('Analysis failed');

      const json = await response.json();
      setAnalysisData(json);
      setPhase('visualize');
      setCurrentChartIndex(0);
    } catch (error) {
      console.error('WhatsApp analysis error:', error);
      setError('Analysis failed');
      setPhase('upload');
    }
  };

  const handleScroll = (e) => {
    if (isScrolling || !analysisData) return;

    const chartDataSets = buildChartDataSets(analysisData);
    if (!chartDataSets.length) return;

    setIsScrolling(true);

    if (e.deltaY > 0 && currentChartIndex < chartDataSets.length - 1) {
      setCurrentChartIndex(prev => prev + 1);
    } else if (e.deltaY < 0 && currentChartIndex > 0) {
      setCurrentChartIndex(prev => prev - 1);
    }

    setTimeout(() => setIsScrolling(false), 700);
  };

  if (phase === 'upload') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 flex items-center justify-center p-4">
        <div className="w-full max-w-xl p-8 bg-white/70 backdrop-blur-sm rounded-xl shadow-lg">
          {error && (
            <div className="mb-6 p-4 bg-red-50 text-red-700 rounded-lg">
              {error}
            </div>
          )}

          <h1 className="text-3xl font-bold text-center mb-8">WhatsApp Chat Analysis</h1>

          {!connectionMethod ? (
            <div className="space-y-4">
              <button
                onClick={() => setConnectionMethod('file')}
                className="w-full p-6 text-left border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 transition-colors"
              >
                <h2 className="text-xl font-semibold mb-2">Upload Chat File</h2>
                <p className="text-gray-600">Export your chat from WhatsApp and upload the file</p>
              </button>

              <button
                onClick={() => setConnectionMethod('whatsapp')}
                className="w-full p-6 text-left border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 transition-colors"
              >
                <h2 className="text-xl font-semibold mb-2">Connect to WhatsApp</h2>
                <p className="text-gray-600">Connect directly using QR code</p>
              </button>
            </div>
          ) : (
            <div>
              <button
                onClick={() => {
                  setConnectionMethod(null);
                  setError(null);
                }}
                className="mb-6 text-gray-600 hover:text-gray-800"
              >
                 Back to options
              </button>

              {connectionMethod === 'file' ? (
                <div className="space-y-4">
                  <label className="block w-full px-4 py-8 border-2 border-dashed border-gray-300 rounded-lg text-center cursor-pointer hover:border-blue-500 transition-colors">
                    <input
                      type="file"
                      accept=".txt"
                      onChange={handleFileChange}
                      className="hidden"
                    />
                    <span className="text-gray-600">
                      {selectedFile ? selectedFile.name : "Drop your WhatsApp chat file here or click to browse"}
                    </span>
                  </label>

                  {selectedFile && (
                    <button
                      onClick={handleFileUpload}
                      className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      Analyze Chat
                    </button>
                  )}
                </div>
              ) : (
                <WhatsAppConnector onChatSelected={handleWhatsAppChat} />
              )}
            </div>
          )}
        </div>
      </div>
    );
  }

  if (phase === 'loading') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 flex flex-col items-center justify-center">
        <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin mb-4" />
        <h2 className="text-xl font-medium text-gray-700">Analyzing your chat...</h2>
      </div>
    );
  }


  if (phase === 'error') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 flex items-center justify-center p-4">
        <div className="bg-red-50 border-l-4 border-red-400 p-4 max-w-md">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">
                There was an error analyzing your chat. Please try again.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (phase === 'visualize') {
    const chartDataSets = buildChartDataSets(analysisData);
    if (!chartDataSets.length) {
      return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 flex items-center justify-center p-4">
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 max-w-md">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-yellow-700">
                  No data available for visualization
                </p>
              </div>
            </div>
          </div>
        </div>
      );
    }

  // Ensure currentChartIndex is within bounds
  const safeCurrentIndex = Math.max(0, Math.min(currentChartIndex, chartDataSets.length - 1));
  const currentChart = chartDataSets[safeCurrentIndex];
  // Only show the scroll indicator if there are more charts to scroll to
  const showScrollIndicator = safeCurrentIndex < chartDataSets.length - 1;

  return (
    <div 
      className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 flex items-center justify-center p-8"
      onWheel={handleScroll}
    >
      <div className="fixed top-4 right-4 bg-white/80 backdrop-blur-sm rounded-full px-4 py-2 text-sm text-gray-600">
        Chart {safeCurrentIndex + 1} of {chartDataSets.length}
      </div>
      {currentChart && (
        <AnimatedChart 
          chart={currentChart}
          isVisible={true}
          index={safeCurrentIndex}
        />
      )}
      <ScrollIndicator showIndicator={showScrollIndicator} />
    </div>
  );
}
}

export default App;
